import React, {Component} from 'react'


// Header
function Header() {
    return <h2>我是Header组件</h2>
}

// Banner
class Banner extends Component {
    render() {
        return <h3>我是Banner组件</h3>
    }
}

// Footer
function Footer() {
    return <h2>我是Footer组件</h2>
}

// 类组件
export default class App extends Component {
    constructor() {
        super();

    }

    render() {
        return (
            <div>
                <Header/>
                <Banner/>
                <Footer/>
            </div>
        );
    }
}
